MobileSiteMap.Script:
add screen WO202000 {
  add container "Measurements" {
    add field "Measurement"
    add field "Description"
    
    add recordAction "Cancel" {
      behavior = Cancel
    }
    add recordAction "Save" {
      behavior = Save
    }    
    add containerAction "Insert" {
      Icon = "system://Plus"  
      behavior = Create
      After = Refresh
    }        
    add containerAction "Delete" {
      Icon = "system://Trash"      
      Behavior = Delete      
      After = Close    
    }    
  }
}
