MobileSiteMap.Script:
add screen WO203000 {
  add container "FailureModes" {
    add field "FailureMode"
    add field "Description"
    
    add recordAction "Cancel" {
      behavior = Cancel
    }
    add recordAction "Save" {
      behavior = Save
    }    
    add recordAction "Insert" {
      Icon = "system://Plus"  
      behavior = Create
      After = Refresh
    }        
    add recordAction "Delete" {
      Icon = "system://Trash"      
      Behavior = Delete      
      After = Close    
    }    
  }
}
